package wfa

import (
	"bytes"
	"fmt"
	"os"
	"testing"
)

func TestWFA(_t *testing.T) {
	algn := New(
		&Penalties{
			Mismatch: 4,
			GapOpen:  6,
			GapExt:   2,
		},
		&Options{
			GlobalAlignment: true,
		})
	algn.AdaptiveReduction(&AdaptiveReductionOption{
		MinWFLen:    10,
		MaxDistDiff: 50,
		CutoffStep:  1,
	})

	var q, t []byte

	// from the paper Bioinformatics, 37(4), 2021, 456–463
	// q = []byte("GATACA")
	// t = []byte("GAGATA")

	// from https://aacbb-workshop.github.io/slides/2022/WFA.ISCA.v6.pdf page15.
	q = []byte("ACCATACTCG")
	t = []byte("AGGATGCTCG")

	// t = []byte("ccccccccccccccccccccccaACCATACTCGaaaaaaaaaaaaaaaaaaaaa")
	// q = []byte("AGGATGCTCG")

	// demo in readme
	// q = []byte("ACGATCTCG")
	// t = []byte("CAGGCTCCTCGG")

	// from https://aacbb-workshop.github.io/slides/2022/WFA.ISCA.v6.pdf page15.
	// q = []byte("TCTTTACTCGCGCGTTGGAGAAATACAATAGT")
	// t = []byte("TCTATACTGCGCGTTTGGAGAAATAAAATAGT")

	// error rate 0.05
	// q = []byte("")
	// t = []byte("")

	// 1-kb seq, error rate 0.2
	// q = []byte("")
	// t = []byte("")

	// 2-kb seq
	// q = []byte("")
	// t = []byte("")

	// from https://github.com/smarco/WFA2-lib
	//    PATTERN    AGCTA-GTGTCAATGGCTACT---TTTCAGGTCCT
	//               | ||| |||||  ||||||||   | |||||||||
	//    TEXT       AACTAAGTGTCGGTGGCTACTATATATCAGGTCCT
	//    ALIGNMENT  1M1X3M1I5M2X8M3I1M1X9M
	// q = []byte("AGCTAGTGTCAATGGCTACTTTTCAGGTCCT")
	// t = []byte("AACTAAGTGTCGGTGGCTACTATATATCAGGTCCT")

	// complicated path
	// q = []byte("ATTAAAATTATCATAACCAGTTGTCTGACATTTATAAAAATAAAATACCGCTGACCGCAGACTACGAGTCGGGGTTGTATGCTAGAGAGTGGACTGAAC")
	// t = []byte("GAATTAAAATTATCATAACCAGTTGTCTGATCATATAAAAATAAAATACCGCTGACCGCAGACTACGAGTCGGGGTTGTATGCTAGAGAGTGGACTGAAC")

	// q = []byte("CTTAAAATTATCATAACCAGTTGTCTGACATTTATAAAAATAAAATACCGCTGACCGCAGACTACGAGTCGGGGTTGTATGCTAGAGAGTGGACTGAAC")
	// t = []byte("GAGTTAAAATTATCATAACCAGTTGTCTGATCATATAAAAATAAAATACCGCTGACCGCAGACTACGAGTCGGGGTTGTATGCTAGAGAGTGGACTGAAC")

	// 1X99M
	// q = []byte("ACTATAAGCGTCCTCTGCGAGACCGGATGCGTTGATGACAGCGAATTGAGTTGAACTCCCTAAGGACACTCAATAATATTGGTCTATGCAAAAAGTCATT")
	// t = []byte("CCTATAAGCGTCCTCTGCGAGACCGGATGCGTTGATGACAGCGAATTGAGTTGAACTCCCTAAGGACACTCAATAATATTGGTCTATGCAAAAAGTCATT")

	// special cases: CG vs G, GC vs G.
	// q = []byte("CG")
	// t = []byte("C")

	// q = []byte("C")
	// t = []byte("C")

	// distant sequences
	// q = []byte("ATTGGAAAATAGGATTGGGGTTTGTTTATATTTGGGTTGAGGGATGTCCCACCTTCGTCGTCCTTACGTTTCCGGAAGGGAGTGGTTAGCTCGAAGCCCA")
	// t = []byte("CCGTAGAGTTAGACACTCGACCGTGGTGAATCCGCGACCACCGCTTTGACGGGCGCTCTACGGTATCCCGCGATTTGTGTACGTGAAGCAGTGATTAAAC")

	// distant sequences
	q = []byte("GAGTGGTTAGCTCGAAGCCCA")
	t = []byte("TACGTGAAGCAGTGATTAAAC")

	// q = []byte("ATTGGAAAATAGGATTGGGGTTTGTTTATATTTGGGTTGAGGGATGTCCCACCTTCGTCGTCCTTACGTTTCCGGAAGGGAGTGGTTAGCTCGAAGCCCA")
	// t = []byte("GATTGGAAAATAGGATGGGGTTTGTTTATATTTGGGTTGAGGGATGTCCCACCTTGTCGTCCTTACGTTTCCGGAAGGGAGTGGTTGCTCGAAGCCCA")

	// q = []byte("")
	// t = []byte("")

	// q = []byte("")
	// t = []byte("")

	// complicated region
	// q = []byte("TACATTTTTACCTATGTGAGATATTTCACATAAACTTAGTACTACTATGAGTTTGTTCGTGTATGATGGGGTCGAC")
	// t = []byte("TACATTTTTACCTATGTGAGATATTTCACATAAACTTAGTATAAATGAGTTGTTCGTGTATGATGGGGTCGACGA")

	// boundary checking
	// q = []byte("ACTG")
	// t = []byte("ACTGA")

	q = []byte("Bioinformatics helps Biology")
	t = []byte("I learn bioinformatics to help biologists")

	// t = []byte("")
	// q = []byte("")

	q = bytes.ToUpper(q)
	t = bytes.ToUpper(t)
	result, err := algn.Align(q, t)
	if err != nil {
		_t.Error(err)
		return
	}

	// fmt.Printf("\n---------------- M ----------------\n")
	// algn.M.Print(os.Stdout, "M")
	fmt.Fprintln(os.Stdout)
	algn.Plot(&q, &t, os.Stdout, algn.M, true, -1)

	// fmt.Printf("\n---------------- I ----------------\n")
	// algn.M.Print(os.Stdout, "I")
	// algn.Plot(&q, &t, os.Stdout, algn.I, true, -1)

	// fmt.Printf("\n---------------- D ----------------\n")
	// algn.M.Print(os.Stdout, "D")
	// algn.Plot(&q, &t, os.Stdout, algn.D, true, -1)

	if result != nil {
		fmt.Println()
		fmt.Printf("CIGAR:  %s\n", result.CIGAR())
		Q, A, T := result.AlignmentText(&q, &t)
		fmt.Printf("query   %s\n", *Q)
		fmt.Printf("        %s\n", *A)
		fmt.Printf("target  %s\n", *T)

		fmt.Println()
		fmt.Printf("align-score : %d\n", result.Score)
		fmt.Printf("match-region: q[%d, %d]/%d vs t[%d, %d]/%d\n",
			result.QBegin, result.QEnd, len(q), result.TBegin, result.TEnd, len(t))
		fmt.Printf("align-length: %d, matches: %d (%.2f%%), gaps: %d, gapRegions: %d\n",
			result.AlignLen, result.Matches, float64(result.Matches)/float64(result.AlignLen)*100,
			result.Gaps, result.GapRegions)
		fmt.Println()

		RecycleAlignmentText(Q, A, T)
		RecycleAlignmentResult(result)
	}
	RecycleAligner(algn)
}
